def pinbow(a: int, b: int, m: int):
        '''
                return a^b mod m
        '''
        a %= m
        res = 1
        while (b > 0):
                '''
                        当b的当前二进制最低位为1时，即对应着二进制指数不为0时的Xi时
                                平方模m
                '''
                if (b & 1): 
                        res = res * a % m
                a = a * a % m
                b >>= 1
        return res

